(function ($) {
    "use strict";



    /*
    -------------------
    List item active
    -------------------*/
    $('.icons')
        .on('click', function () {
            $(".icons.active")
                .removeClass("active");
            $(this)
                .addClass('active');
        });

    $(".icons")
        .on("click", function (event) {
            event.stopPropagation();
        });

    $(document)
        .on("click", function () {
            $(".icons")
                .removeClass("active");

        });


    /* TO DO LIST
    --------------------*/
    $(".tdl-new")
        .on('keypress', function (e) {
            var code = (e.keyCode ? e.keyCode : e.which);
            if (code == 13) {
                var v = $(this)
                    .val();
                var s = v.replace(/ +?/g, '');
                if (s == "") {
                    return false;
                } else {
                    $(".tdl-content ul")
                        .append("<li><label><input type='checkbox'><i></i><span>" + v + "</span><a href='#' class='ti-close'></a></label></li>");
                    $(this)
                        .val("");
                }
            }
        });


    $(".tdl-content a")
        .on("click", function () {
            var _li = $(this)
                .parent()
                .parent("li");
            _li.addClass("remove")
                .stop()
                .delay(100)
                .slideUp("fast", function () {
                    _li.remove();
                });
            return false;
        });

    // for dynamically created a tags
    $(".tdl-content")
        .on('click', "a", function () {
            var _li = $(this)
                .parent()
                .parent("li");
            _li.addClass("remove")
                .stop()
                .delay(100)
                .slideUp("fast", function () {
                    _li.remove();
                });
            return false;
        });



    if ($('#webticker-dark-icons')
        .length) {
        $("#webticker-dark-icons")
            .webTicker({
                height: 'auto',
                // duplicate: true,
                startEmpty: false,
                // rssfrequency: 5
            });
    }




})(jQuery);
